mapper.select:
SELECT * FROM attractions
ORDER BY RAND()
LIMIT #{limit}

SELECT a.* FROM attractions a LEFT JOIN tb_cities c ON a.city_id = c.id
AND ( a.title LIKE CONCAT('%', #{keyword}, '%') OR a.tag LIKE CONCAT('%', #{keyword}, '%') OR c.name LIKE CONCAT('%', #{keyword}, '%') )
LIMIT #{limit} OFFSET #{offset}
